function isVisibleOnScreen(a){var t=$(a),c=$(window),n=c.scrollTop(),i=n+c.height(),r=t.offset().top,o=r+10;return i>o&&r>n}function loadBg(a){background=a.attr("data-src"),a.css("background-image","url("+background+")"),a.removeAttr("data-src")}function loadImg(a){a.attr("src",a.attr("data-src")),a.removeAttr("data-src")}function checkLazy(){$(".bg-lazy[data-src]").length>0&&$(".bg-lazy[data-src]").each(function(){isVisibleOnScreen($(this))&&loadBg($(this))}),$("img.img-lazy[data-src]").length>0&&$("img.img-lazy[data-src]").each(function(){isVisibleOnScreen($(this))&&loadImg($(this))})}$(document).ready(function(){$(window).scroll(function(){checkLazy()}),$(window).load(function(){checkLazy()})});